function q1() {

    var x = document.getElementById("ans1");
    var y = document.getElementById("q1");

    if (x.style.display === "block") {
        x.style.display = "none";
        y.innerText = "Mostrar";
    } else {
        x.style.display = "block";
        y.innerText = "Ocultar"
    }
}

